import React from 'react';
import { endpoints_properties } from '../../properties/EndPointsProperties.js';
import { api_properties } from '../../properties/APIProperties.js';
// reactstrap components
import {
  Button,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col,
  Alert,
  ButtonGroup
} from 'reactstrap';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Card, CardBody, CardText, FormControl, FormLabel, FormCheck } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAlignRight, faInfoCircle } from '@fortawesome/free-solid-svg-icons';
import "./../../css/tooltip.scss";
import { OverlayTrigger, Tooltip } from "react-bootstrap";

class RegistrationComponent extends React.Component {
  componentDidMount() {
    document.documentElement.scrollTop = 0;
    document.scrollingElement.scrollTop = 0;
    this.refs.main.scrollTop = 0;
  }
  constructor(props) {
    super(props);
    this.state = {
      user: 0,
      isServiceProvider: false,
      isServiceConsumer: false,
      isTrainer: false,
      isTrainee: false,
      defaultRole: 'none',
      gts_user_id: '',
      email: '',
      password: '',
      role: '',
      confirmPassword: '',
      registrationError: '',
      registrationSuccess: '',
      buttonDisabled: false,
      message: '',
      isError: '',
      errors: {},
      responses: {},
      visible: false,
      roleError: '',
      defaultRoleError: '',
      roleSuccess: '',
    };
    this.onRadioChange = this.onRadioChange.bind(this);
  }



  onRadioChange = (e) => {
    if (e.target.value == "ServiceProvider") {
      this.setState({
        defaultRole: e.target.value,
        isServiceProvider: true
      });
    }
    if (e.target.value == "ServiceConsumer") {
      this.setState({
        defaultRole: e.target.value,
        isServiceConsumer: true
      });
    }
    if (e.target.value == "Trainee") {
      this.setState({
        defaultRole: e.target.value,
        isTrainee: true
      });
    }
    if (e.target.value == "Trainer") {
      this.setState({
        defaultRole: e.target.value,
        isTrainer: true
      });
    }
  }
  //analyse why we need this?
  toggleChangeServiceProvider = () => {
    this.setState(prevState => ({
      isServiceProvider: !prevState.isServiceProvider
    }));
  }

  toggleChangeServiceConsumer = () => {
    this.setState(prevState => ({
      isServiceConsumer: !prevState.isServiceConsumer
    }));
  }

  toggleChangeTrainee = () => {
    this.setState(prevState => ({
      isTrainee: !prevState.isTrainee
    }));
  }

  toggleChangeTrainer = () => {
    this.setState(prevState => ({
      isTrainer: !prevState.isTrainer
    }));
  }

  onSubmit = (e) => {
    e.preventDefault();
    console.log(this.state);
  }

  inputOnChangeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  clearEmailError = e => {
    let responses = this.state.responses;
    let errors = this.state.errors;
    let formIsValid = true;

    if (e.target.name == "email") {
      errors["email"] = "";
      this.setState({
        errors: errors
      });
      return formIsValid;
    }

    if (e.target.name == "registrationError") {
      responses["registrationError"] = "";
      this.setState({
        responses: responses
      });
      return formIsValid;
    }
  }

  clearPasswordError = e => {
    let responses = this.state.responses;
    let errors = this.state.errors;
    let formIsValid = true;


    if (e.target.name == "password") {
      errors["password"] = "";
      this.setState({
        errors: errors
      });
      return formIsValid;
    }

    if (e.target.name == "registrationError") {
      responses["registrationError"] = "";
      this.setState({
        responses: responses
      });
      return formIsValid;
    }
  }

  clearConfirmPasswordError = e => {
    let responses = this.state.responses;
    let errors = this.state.errors;
    let formIsValid = true;


    if (e.target.name == "confirmPassword") {
      errors["confirmPassword"] = "";
      this.setState({
        errors: errors
      });
      return formIsValid;
    }

    if (e.target.name == "registrationError") {
      responses["registrationError"] = "";
      this.setState({
        responses: responses
      });
      return formIsValid;
    }
  }

  validateEmail = e => {
    let responses = this.state.responses;
    let errors = this.state.errors;
    let formIsValid = true;


      responses["registrationError"] = "";
      this.setState({
        responses: responses
      });
      return formIsValid;

    if (!this.state.email) {
      formIsValid = false;
      errors["email"] = "Please enter your email-ID.";
      this.setState({
        errors: errors
      });
      return formIsValid;
    }

    if (typeof this.state.email !== "undefined") {
      var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);

      if (!pattern.test(this.state.email)) {
        formIsValid = false;
        errors["email"] = "Please enter valid email-ID.";
      }
      this.setState({
        errors: errors
      });
      return formIsValid;
    }
  }

  validatePassword = e => {
    let errors = this.state.errors;
    let formIsValid = true;

    if (!this.state.password) {
      formIsValid = false;
      errors["password"] = "Please enter your password.";
      this.setState({
        errors: errors
      });
      return formIsValid;
    }

    if (typeof this.state.password !== "undefined") {
      if (!this.state.password.match(/^.*(?=.{8,20})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&+=*><?_~]).*$/)) {
        formIsValid = false;
        errors["password"] = "Please enter secure and strong password .";
        this.setState({
          errors: errors
        });
        return formIsValid;
      }
    }
  }

  validateConfirmPassword = e => {
    let errors = this.state.errors;
    let formIsValid = true;
    let responses = this.state.responses;

    if (!this.state.confirmPassword) {
      formIsValid = false;
      errors["confirmPassword"] = "Please confirm your  password.";
      this.setState({
        errors: errors
      });
      return formIsValid;
    }

    if (typeof this.state.confirmPassword !== "undefined") {
      if (!this.state.confirmPassword.match(/^.*(?=.{8,20})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&+=*><?_~]).*$/)) {
        formIsValid = false;
        errors["confirmPassword"] = "Please enter secure and strong password .";
        this.setState({
          errors: errors
        });
        return formIsValid;
      }

      if (this.state.password != this.state.confirmPassword) {
        errors["confirmPassword"] = "Password & Confirm-Password must be same.";
        this.setState({
          errors: errors
        });
        return formIsValid;
      }
    }
    if (e.target.name == "registrationError") {
      responses["registrationError"] = "";
      this.setState({
        responses: responses
      });
      return formIsValid;
    }
  }

